/**
 * Twitch.Tv Node.js Client example
 * Get a list of games, teams, ingests, and streams without parameters
 */
 
var TwitchTv = require('../lib/twitch')
  , twitch = new TwitchTv;

twitch.getGames(function(data) {
  console.log("Getting Twitch.tv Games...");
  console.log(data);
});

twitch.getTeams(function(data) {
  console.log("Getting Twitch.tv Teams...");
  console.log(data);
});

twitch.getIngests(function(data) {
  console.log("Getting Twitch.tv Ingests...");
  console.log(data);
});

twitch.getStreams(function(data) {
  console.log("Getting Twitch.tv Streams...");
  console.log(data._links);
});
